sys_script_email.script:
(function runMailScript( /* GlideRecord */ current, /* TemplatePrinter */ template,
    /* Optional EmailOutbound */
    email, /* Optional GlideRecord */ email_action, /* Optional GlideRecord */ event) {

    // Return proper link to Event Registration portal page
	var baseURL = gs.getProperty('glide.servlet.uri');
	template.print(baseURL + 'now/' + 'x_snc_we_volunteer-event_registration/event_details/x_snc_we_volunteer_event/' + current.event_name.sys_id);

})(current, template, email, email_action, event);
