sys_sg_write_back_action_item.execution_script:
(function WriteBackAction(input) {
	//Type appropriate comment here, and begin script below
	//gs.info("input waitlist "+JSON.stringify(input));
	var eventSysId = '';
	var eventTable = new GlideRecord('x_snc_we_volunteer_event');
	if(eventTable.get(input.sys_id)){
		eventSysId = input.sys_id;
	}else{
		var attendeeGr1 = new GlideRecord('x_snc_we_volunteer_attendee');
		if(attendeeGr1.get(input.sys_id)){
			eventSysId = attendeeGr1.event_name.sys_id;
		}
	}
	if(eventSysId){
		var gr_attendee = new GlideRecord("x_snc_we_volunteer_attendee");
		gr_attendee.newRecord();
		gr_attendee.attendee_name = gs.getUserID();
		gr_attendee.event_name = eventSysId;
		gr_attendee.insert();
	}

})(input);
